con:config.script:
import com.eviware.soapui.model.testsuite.TestRunner.Status
import groovy.json.JsonSlurper

def slurper = new JsonSlurper()

// -------------------------------------
// Set up data gathering

def tc = testRunner.testCase.testSuite.testCases["Average lifespan"]
def Closure<String> data = {
	slurper.parseText(it).basic
}

// -------------------------------------

// Don't change this
def id = testRunner.testCase.testSuite.project.testSuites["Statistics"].testCases["Get user id"]

def story = [:]
def patrol = [:]
def raid = [:]
def allPvP = [:]
def allStrikes = [:]

def users = testRunner.testCase.getPropertyValue("usernames").tokenize(',')*.trim()

for (String user : users) {
	// get user id
	id.setPropertyValue("name", user)

	// run test synchrouously
	def runner = id.run(null, false)
	def userid = id.getPropertyValue("userid")
	def type = id.getPropertyValue("type")

	tc.setPropertyValue("userid", userid)
	tc.setPropertyValue("type", type)
	tc.run(null, false)

	story.put(user, tc.getPropertyValue("story"))
	patrol.put(user, tc.getPropertyValue("patrol"))
	raid.put(user, tc.getPropertyValue("raid"))
	allPvP.put(user, tc.getPropertyValue("allPvP"))
	allStrikes.put(user, tc.getPropertyValue("allStrikes"))
}

StringBuilder output = new StringBuilder()

output.append(format("Story", story, data)).append("\n")
output.append(format("Patrol", patrol, data)).append("\n")
output.append(format("Raid", raid, data)).append("\n")
output.append(format("All strikes", allStrikes, data)).append("\n")
output.append(format("All PvP", allPvP, data))
output.append("\n")

def result = output.toString()
log.info result
testRunner.testCase.setPropertyValue("result", result) // Used in teardown script

String format(String name, Map<String, String> values, Closure<String> get) {
	StringBuilder output = new StringBuilder();
	output.append(sprintf("[b]%s[/b]\n", name))

	// Sort by raw value...
	values.sort({ a, b -> Double.valueOf(get(b.value).value) <=> Double.valueOf(get(a.value).value) }).each {
		// ...but display pretty "display value"
		output.append(sprintf("%s: %s\n", it.key, get(it.value).displayValue))
	}

	return output.toString();
}

con:testCase.con:tearDownScript:
def filename = testRunner.testCase.getPropertyValue("outputfile")

if (filename != null) {
	def outputFile = new File(filename)
	def result = testRunner.testCase.getPropertyValue("result")
	outputFile.write(result)
}

testRunner.testCase.setPropertyValue("result", "")
